function PatientDashboard({ patient }) {
  const [appointments, setAppointments] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!patient) return; // <-- Guard: do nothing if patient is undefined
    const loadAppointments = async () => {
      try {
        const data = await fetchPatientAppointments(patient.id);
        setAppointments(data);
      } catch (err) {
        setError(err.message);
      }
    };
    loadAppointments();
  }, [patient]);

  if (!patient) return <p>Loading patient data...</p>; // <-- Show placeholder while undefined

  return (
    <div style={{ maxWidth: "800px", margin: "20px auto" }}>
      <h2>Welcome, {patient.name}</h2>
      <button style={{ marginBottom: "20px" }}>Book New Appointment</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <h3>Upcoming Appointments</h3>
      {appointments.length === 0 ? (
        <p>No upcoming appointments</p>
      ) : (
        <table border="1" width="100%" cellPadding="10">
          <thead>
            <tr>
              <th>Doctor</th>
              <th>Date</th>
              <th>Time</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((appt) => (
              <tr key={appt.id}>
                <td>{appt.doctorName}</td>
                <td>{appt.date}</td>
                <td>{appt.time}</td>
                <td>{appt.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
