import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { fetchDoctors, createAppointment } from "../utils/api";

// Helper to convert 24-hour to 12-hour format
function formatTo12Hour(time24) {
  if (!time24) return "";
  let [hours, minutes] = time24.split(":").map(Number);
  const ampm = hours >= 12 ? "PM" : "AM";
  hours = hours % 12 || 12; // convert 0 -> 12 for midnight
  return `${hours}:${minutes.toString().padStart(2, "0")} ${ampm}`;
}

function BookAppointment() {
  const location = useLocation();
  const navigate = useNavigate();
  const patient = location.state?.patient;

  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [specializationFilter, setSpecializationFilter] = useState("");

  const [form, setForm] = useState({
    doctorId: "",
    date: "",
    time: "",   // selected appointment time in 24-hour format
    reason: ""
  });

  const [message, setMessage] = useState("");

  const specializations = [
    "Cardiology",
    "Dermatology",
    "Neurology",
    "Orthopedics",
    "Pediatrics",
    "General Medicine"
  ];

  // Load doctors
  useEffect(() => {
    const loadDoctors = async () => {
      try {
        const data = await fetchDoctors();
        setDoctors(data);
        setFilteredDoctors(data);
      } catch (err) {
        setMessage("Failed to load doctors");
      }
    };
    loadDoctors();
  }, []);

  // Apply filters
  useEffect(() => {
    let filtered = doctors;
    if (specializationFilter) {
      filtered = filtered.filter(
        doc => doc.specialization.toLowerCase() === specializationFilter.toLowerCase()
      );
    }
    setFilteredDoctors(filtered);
  }, [specializationFilter, doctors]);

  if (!patient) {
    navigate("/");
    return null;
  }

  // When user selects doctor, set default time automatically
  const handleDoctorChange = (e) => {
    const doctorId = e.target.value;
    const selectedDoctor = doctors.find(doc => doc.id === parseInt(doctorId));
    setForm({
      ...form,
      doctorId,
      time: selectedDoctor?.openingTime || "09:00" // use openingTime as default
    });
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createAppointment({
        patientId: patient.id,
        doctorId: form.doctorId,
        appointmentDate: form.date,
        appointmentTime: form.time, // send 24-hour format
        reason: form.reason
      });
      setMessage("Appointment booked successfully!");
      setTimeout(() => navigate("/dashboard", { state: { patient } }), 1500);
    } catch (error) {
      setMessage(error.message || "Failed to book appointment");
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>Book Appointment</h2>

      {/* Filters */}
      <div style={{ marginBottom: "20px", border: "1px solid #ccc", padding: "10px" }}>
        <h4>Search Doctors</h4>
        <select
          value={specializationFilter}
          onChange={(e) => setSpecializationFilter(e.target.value)}
        >
          <option value="">-- Select Specialization --</option>
          {specializations.map(spec => (
            <option key={spec} value={spec}>{spec}</option>
          ))}
        </select>
      </div>

      {/* Appointment Form */}
      <form onSubmit={handleSubmit}>
        <label>Doctor:</label><br />
        <select
          name="doctorId"
          value={form.doctorId}
          onChange={handleDoctorChange}
          required
        >
          <option value="">-- Select Doctor --</option>
          {filteredDoctors.map((doc) => (
            <option key={doc.id} value={doc.id}>
              {doc.name} ({doc.specialization}) - {doc.availability} 
              ({formatTo12Hour(doc.openingTime)} - {formatTo12Hour(doc.closingTime)})
            </option>
          ))}
        </select><br /><br />

        <label>Date:</label><br />
        <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
          required
        /><br /><br />

        <label>Appointment Time:</label><br />
        <input
          type="text"
          value={formatTo12Hour(form.time)}
          readOnly
        /><br /><br />

        <label>Reason:</label><br />
        <textarea
          name="reason"
          value={form.reason}
          onChange={handleChange}
          placeholder="Enter reason for appointment"
        /><br /><br />

        <button type="submit">Book Appointment</button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}

export default BookAppointment;
