import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { fetchDoctors, createAppointment } from "../utils/api";

function BookAppointment() {
  const location = useLocation();
  const navigate = useNavigate();
  const patient = location.state?.patient;

  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [specializationFilter, setSpecializationFilter] = useState("");
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [availableDates, setAvailableDates] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);

  const [form, setForm] = useState({
    doctorId: "",
    date: "",
    time: "",
    reason: ""
  });

  const [message, setMessage] = useState("");

  // Predefined specializations
  const specializations = [
    "Cardiology",
    "Dermatology",
    "Neurology",
    "Orthopedics",
    "Pediatrics",
    "General Medicine"
  ];

  // Load doctors from API
  useEffect(() => {
    const loadDoctors = async () => {
      try {
        const data = await fetchDoctors();
        setDoctors(data);
        setFilteredDoctors(data);
      } catch (err) {
        setMessage("Failed to load doctors");
      }
    };
    loadDoctors();
  }, []);

  // Filter doctors by specialization
  useEffect(() => {
    let filtered = doctors;
    if (specializationFilter) {
      filtered = filtered.filter(
        (doc) =>
          doc.specialization.toLowerCase() === specializationFilter.toLowerCase()
      );
    }
    setFilteredDoctors(filtered);
    setSelectedDoctor(null);
    setAvailableDates([]);
    setAvailableTimes([]);
  }, [specializationFilter, doctors]);

  if (!patient) {
    navigate("/"); // redirect if not logged in
    return null;
  }

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleDoctorChange = (e) => {
    const doctor = filteredDoctors.find((d) => d.id.toString() === e.target.value);
    setSelectedDoctor(doctor);
    setForm({ ...form, doctorId: e.target.value, date: "", time: "" });

    // Compute available dates for next 14 days
    const dates = [];
    const today = new Date();
    for (let i = 0; i < 14; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      const dayName = date
        .toLocaleDateString("en-US", { weekday: "long" })
        .toUpperCase();
      if (doctor.dailyAvailability && doctor.dailyAvailability[dayName]) {
        if (doctor.dailyAvailability[dayName].trim() !== "") {
          dates.push(date.toISOString().split("T")[0]);
        }
      }
    }
    setAvailableDates(dates);
    setAvailableTimes([]);
  };

  const handleDateChange = (e) => {
    const date = e.target.value;
    setForm({ ...form, date, time: "" });

    if (!selectedDoctor) return;

    const dayName = new Date(date)
      .toLocaleDateString("en-US", { weekday: "long" })
      .toUpperCase();

    const slots = selectedDoctor.dailyAvailability[dayName]; // e.g., "09:00-12:00,14:00-17:00"
    if (slots) {
      const times = [];
      slots.split(",").forEach((slot) => {
        const [start, end] = slot.split("-");
        let current = start;
        while (current < end) {
          times.push(current);
          // increment 30 min
          const [h, m] = current.split(":").map(Number);
          let next = new Date();
          next.setHours(h);
          next.setMinutes(m + 30);
          const hh = next.getHours().toString().padStart(2, "0");
          const mm = next.getMinutes().toString().padStart(2, "0");
          current = `${hh}:${mm}`;
          if (current === end) break;
        }
      });
      setAvailableTimes(times);
    } else {
      setAvailableTimes([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.doctorId || !patient?.id) {
      setMessage("Please select a doctor and make sure you are logged in");
      return;
    }

    try {
      await createAppointment({
        patientId: patient.id,
        doctorId: Number(form.doctorId),
        appointmentDate: form.date,
        appointmentTime: form.time,
        reason: form.reason
      });
      setMessage("Appointment booked successfully!");
      setTimeout(() => navigate("/dashboard", { state: { patient } }), 1500);
    } catch (error) {
      setMessage(error.message || "Failed to book appointment");
    }
  };

  // Convert 24-hour time to 12-hour format
  const to12HourFormat = (time) => {
    const [h, m] = time.split(":").map(Number);
    const ampm = h >= 12 ? "PM" : "AM";
    const hour12 = h % 12 === 0 ? 12 : h % 12;
    return `${hour12}:${m.toString().padStart(2, "0")} ${ampm}`;
  };

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>Book Appointment</h2>

      {/* Specialization Filter */}
      <div style={{ marginBottom: "20px" }}>
        <select
          value={specializationFilter}
          onChange={(e) => setSpecializationFilter(e.target.value)}
        >
          <option value="">-- Select Specialization --</option>
          {specializations.map((spec) => (
            <option key={spec} value={spec}>
              {spec}
            </option>
          ))}
        </select>
      </div>

      {/* Appointment Form */}
      <form onSubmit={handleSubmit}>
        <label>Doctor:</label>
        <br />
        <select
          name="doctorId"
          value={form.doctorId}
          onChange={handleDoctorChange}
          required
        >
          <option value="">-- Select Doctor --</option>
          {filteredDoctors.map((doc) => (
            <option key={doc.id} value={doc.id}>
              {doc.name} ({doc.specialization})
            </option>
          ))}
        </select>
        <br />
        <br />

        <label>Date:</label>
        <br />
        <select
          name="date"
          value={form.date}
          onChange={handleDateChange}
          required
        >
          <option value="">-- Select Date --</option>
          {availableDates.map((d) => (
            <option key={d} value={d}>
              {d}
            </option>
          ))}
        </select>
        <br />
        <br />

        <label>Time:</label>
        <br />
        <select
          name="time"
          value={form.time}
          onChange={handleChange}
          required
        >
          <option value="">-- Select Time --</option>
          {availableTimes.map((t) => (
            <option key={t} value={t}>
              {to12HourFormat(t)}
            </option>
          ))}
        </select>
        <br />
        <br />

        <label>Reason:</label>
        <br />
        <textarea
          name="reason"
          value={form.reason}
          onChange={handleChange}
          placeholder="Enter reason for appointment"
        />
        <br />
        <br />

        <button type="submit">Book Appointment</button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}

export default BookAppointment;
