// AppointmentForm.js
import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { fetchDoctors, createAppointment } from "../utils/api";

function AppointmentForm() {
  const navigate = useNavigate();
  const location = useLocation();
  const patient = location.state?.patient;

  const [doctors, setDoctors] = useState([]);
  const [form, setForm] = useState({ doctor: "", date: "", time: "" });
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (!patient) {
      navigate("/"); // redirect if no patient
      return;
    }
    fetchDoctors().then(setDoctors);
  }, [patient, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createAppointment({
        patientId: patient.id,
        doctorId: form.doctor,
        date: form.date,
        time: form.time,
      });
      setMessage("Appointment booked successfully!");
      setTimeout(() => navigate("/dashboard", { state: { patient } }), 1500);
    } catch (error) {
      setMessage(error.message || "Failed to book appointment");
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "20px auto" }}>
      <h2>Book Appointment</h2>
      <form onSubmit={handleSubmit}>
        <label>Doctor:</label>
        <select name="doctor" value={form.doctor} onChange={handleChange} required>
          <option value="">Select Doctor</option>
          {doctors.map((d) => (
            <option key={d.id} value={d.id}>{d.name}</option>
          ))}
        </select>
        <br /><br />

        <label>Date:</label>
        <input type="date" name="date" value={form.date} onChange={handleChange} required />
        <br /><br />

        <label>Time:</label>
        <input type="time" name="time" value={form.time} onChange={handleChange} required />
        <br /><br />

        <button type="submit">Book</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default AppointmentForm;
