import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { fetchDoctors, createAppointment } from "../utils/api";

function BookAppointment() {
  const location = useLocation();
  const navigate = useNavigate();
  const patient = location.state?.patient;

  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [specializationFilter, setSpecializationFilter] = useState("");
  const [availabilityFilter, setAvailabilityFilter] = useState("");

  const [form, setForm] = useState({
    doctorId: "",
    date: "",
    time: "",
    reason: ""
  });
  const [message, setMessage] = useState("");

  // Load doctors
  useEffect(() => {
    const loadDoctors = async () => {
      try {
        const data = await fetchDoctors();
        setDoctors(data);
        setFilteredDoctors(data);
      } catch (err) {
        setMessage("Failed to load doctors");
      }
    };
    loadDoctors();
  }, []);

  // Apply filters whenever specialization or availability changes
  useEffect(() => {
    let filtered = doctors;

    if (specializationFilter) {
      filtered = filtered.filter(doc =>
        doc.specialization.toLowerCase().includes(specializationFilter.toLowerCase())
      );
    }

    if (availabilityFilter) {
      filtered = filtered.filter(doc =>
        availabilityFilter === "available" ? doc.available === true : doc.available === false
      );
    }

    setFilteredDoctors(filtered);
  }, [specializationFilter, availabilityFilter, doctors]);

  if (!patient) {
    navigate("/");
    return null;
  }

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createAppointment({
        patientId: patient.id,
        doctorId: form.doctorId,
        appointmentDate: form.date,
        appointmentTime: form.time,
        reason: form.reason
      });
      setMessage("Appointment booked successfully!");
      setTimeout(() => navigate("/dashboard", { state: { patient } }), 1500);
    } catch (error) {
      setMessage(error.message || "Failed to book appointment");
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>Book Appointment</h2>

      {/* Filters */}
      <div style={{ marginBottom: "20px", border: "1px solid #ccc", padding: "10px" }}>
        <h4>Search Doctors</h4>
        <input
          type="text"
          placeholder="Filter by specialization"
          value={specializationFilter}
          onChange={(e) => setSpecializationFilter(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <select
          value={availabilityFilter}
          onChange={(e) => setAvailabilityFilter(e.target.value)}
        >
          <option value="">All</option>
          <option value="available">Available</option>
          <option value="notAvailable">Not Available</option>
        </select>
      </div>

      {/* Appointment Form */}
      <form onSubmit={handleSubmit}>
        <label>Doctor:</label><br />
        <select
          name="doctorId"
          value={form.doctorId}
          onChange={handleChange}
          required
        >
          <option value="">-- Select Doctor --</option>
          {filteredDoctors.map((doc) => (
            <option key={doc.id} value={doc.id}>
              {doc.name} ({doc.specialization}) {doc.available ? "✅ Available" : "❌ Not Available"}
            </option>
          ))}
        </select><br /><br />

        <label>Date:</label><br />
        <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
          required
        /><br /><br />

        <label>Time:</label><br />
        <input
          type="time"
          name="time"
          value={form.time}
          onChange={handleChange}
          required
        /><br /><br />

        <label>Reason:</label><br />
        <textarea
          name="reason"
          value={form.reason}
          onChange={handleChange}
          placeholder="Enter reason for appointment"
        /><br /><br />

        <button type="submit">Book Appointment</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default BookAppointment;
